<?phpnamespace App\classes;class ServerValidation{	public function is_number($number)	{		if (is_numeric($number))			return true;		return false;	}	public function is_email($email)	{		if (!filter_var($email, FILTER_VALIDATE_EMAIL)) 			return true;				return false;				}	public function is_empty($str)	{  		 $str=$str;		 if (($str == '')||($str==null))			return true;		 return false;	}  public function not_NA($str){  		 $str=trim($str);		 if (($str == 'NA')||($str == 'na'))			return true;		 return false;	}	public function is_mobile($str){				if( !preg_match("/^\d{10}$/", $str) ) {  			return true;		}		return false;	}	  }